$.fn.stars=function(){return $(this).each(function(){var c=parseFloat($(this).attr("size"));var d=parseFloat($(this).html());var b=Math.max(0,(Math.min(5,d)))*c;var a=$("<span />").width(b).height(c).css("background-size",c+"px");$(this).html(a);$(this).css("background-position","0 "+(-c)+"px").height(c).width(5*c).css("background-size",c+"px")})};